import axios from "axios";
import _ from "lodash";
import {useState} from "react";
import useLoading from "./component/useLoading";
require("dotenv").config();
const [isLoading, setIsLoading] = useLoading();

const instance = axios.create({
    baseURL: process.env.REACT_APP_BACKEND_URL,

    //  withCredentials: true
});
if (localStorage.getItem("token")) {
    instance.interceptors.request.use(
        (config) => {
            config.headers.authorization =
                "Bearer " + localStorage.getItem("token").replaceAll('"', "");
                setIsLoading(true); // Đặt tải thành true khi yêu cầu bắt đầu
            return config;
        },
        (error) => {
            setIsLoading(false); // Đặt tải thành false khi có lỗi yêu cầu
            return Promise.reject(error);
        }
    );
}

instance.interceptors.response.use((response) => {
    setIsLoading(false); // Đặt tải thành false khi nhận phản hồi
    // Thrown error for request with OK status code
    const { data } = response;
    return response.data;
}, (error) => {
    setIsLoading(false); // Đảm bảo tải được đặt thành false khi có lỗi phản hồi
    return Promise.reject(error);
});

export default instance;
