import axios from "axios";
import { useContext } from 'react';
import { LoadingContext } from './component/LoadingContext';
require("dotenv").config();

const useCustomAxios = () => {
    const { setIsLoading } = useContext(LoadingContext);

    const instance = axios.create({
        baseURL: process.env.REACT_APP_BACKEND_URL,
    });

    instance.interceptors.request.use(
        (config) => {
            setIsLoading(true); // Start loading
            return config;
        },
        (error) => {
            setIsLoading(false); // Stop loading on request error
            return Promise.reject(error);
        }
    );

    instance.interceptors.response.use(
        (response) => {
            setIsLoading(false); // Stop loading on response
            return response;
        },
        (error) => {
            setIsLoading(false); // Ensure loading is stopped on response error
            return Promise.reject(error);
        }
    );

    return instance;
};

export default useCustomAxios;
